import React, { useState } from 'react';
import { StyleSheet, Text, View, Image, FlatList, TouchableOpacity, TextInput } from 'react-native';

const foodData = [
  {
    id: '1',
    name: 'Burger',
    image: 'data:image/jpeg;base64,',
    price: '$5.99',
  },
  {
    id: '2',
    name: 'Pizza',
    image: 'data:image/jpeg;base64,',
    price: '$9.99',
  },
  {
    id: '3',
    name: 'Sushi',
    image: 'data:image/jpeg;base64,',
    price: '$12.99',
  },
  {
    id: '4',
    name: 'Pasta',
    image: 'data:image/jpeg;base64,',
    price: '$7.49',
  },
  {
    id: '5',
    name: 'Ice Cream',
    image: 'data:image/jpeg;base64,',
    price: '$3.49',
  },
];

const App = () => {
  const [cart, setCart] = useState([]);
  const [search, setSearch] = useState('');

  const handleAddToCart = (item) => {
    setCart((prevCart) => [...prevCart, item]);
  };

  const handleSearchChange = (text) => {
    setSearch(text);
  };

  const filteredData = foodData.filter(item => item.name.toLowerCase().includes(search.toLowerCase()));

  const renderItem = ({ item }) => (
    <View style={styles.foodItem}>
      <Image source={{ uri: item.image }} style={styles.foodImage} />
      <View style={styles.foodDetails}>
        <Text style={styles.foodName}>{item.name}</Text>
        <Text style={styles.foodPrice}>{item.price}</Text>
        <TouchableOpacity style={styles.addButton} onPress={() => handleAddToCart(item)}>
          <Text style={styles.addButtonText}>Add to Cart</Text>
        </TouchableOpacity>
      </View>
    </View>
  );

  return (
    <View style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <Text style={styles.headerText}>Food Delivery</Text>
        <TouchableOpacity style={styles.cartButton}>
          <Text style={styles.cartText}>Cart ({cart.length})</Text>
        </TouchableOpacity>
      </View>

      {/* Search Bar */}
      <TextInput
        style={styles.searchBar}
        placeholder="Search for food..."
        placeholderTextColor="#aaa"
        value={search}
        onChangeText={handleSearchChange}
      />

      {/* Food List */}
      <FlatList
        data={filteredData}
        renderItem={renderItem}
        keyExtractor={(item) => item.id}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#2d3436', // Dark background color
    paddingTop: 20,
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    paddingHorizontal: 20,
    paddingVertical: 10,
    backgroundColor: '#d63031', // Bright red header
    alignItems: 'center',
  },
  headerText: {
    fontSize: 24,
    color: '#fff',
    fontWeight: 'bold',
  },
  cartButton: {
    backgroundColor: '#fff',
    paddingVertical: 8,
    paddingHorizontal: 15,
    borderRadius: 5,
  },
  cartText: {
    fontSize: 16,
    color: '#d63031', // Matching red color
    fontWeight: 'bold',
  },
  searchBar: {
    margin: 15,
    padding: 10,
    backgroundColor: '#333', // Dark search bar background
    borderRadius: 10,
    fontSize: 16,
    color: '#fff',
  },
  foodItem: {
    flexDirection: 'row',
    backgroundColor: '#34495e', // Dark gray background for items
    margin: 10,
    borderRadius: 10,
    padding: 10,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 5,
    elevation: 3,
  },
  foodImage: {
    width: 100,
    height: 100,
    borderRadius: 10,
  },
  foodDetails: {
    flex: 1,
    marginLeft: 15,
    justifyContent: 'space-between',
  },
  foodName: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#ecf0f1', // Light text color
  },
  foodPrice: {
    fontSize: 16,
    color: '#95a5a6', // Light gray color
  },
  addButton: {
    backgroundColor: '#d63031', // Red button
    paddingVertical: 8,
    paddingHorizontal: 15,
    borderRadius: 5,
  },
  addButtonText: {
    fontSize: 16,
    color: '#fff',
    fontWeight: 'bold',
  },
});

export default App;


