import React, { useState } from 'react';
import { StyleSheet, Text, View, Image, FlatList, TouchableOpacity, TextInput } from 'react-native';

const data = [
  {
    id: '1',
    name: 'Saim',
    image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSoCZzERTRyFpUcwgF5Bhrq2gqdO4htZM7rhg&s',
    text: 'This is a post from Saim. Find your dream house.',
    postImage: 'data:image/jpeg;base64,', 
  },
  {
    id: '2',
    name: 'Sherry',
    image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTGlBDd3Y8StB-pVM7SgK292Z-Hd3L6xCTKJA&s',
    text: 'Hello from Sherry ! AI is not just about tech, its about understanding what makes us human',
    postImage: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTP5MDyRSOvTqzcyO__fL-kCF9WjijO3SAIDA&s', 
  },
  {
    id: '3',
    name: 'Rose',
    image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ-Zs_IlgbfZZKZwkCCEc2cxkyL4aabhCtGTg&s',
    text: 'Here is a post by Rose. Physiotherapists can diagnose and treat a broad range of medical conditions and injuries.',
    postImage: 'data:image/jpeg;base64,', 
  },
  
];

const App = () => {
  const [comments, setComments] = useState({});
  const [liked, setLiked] = useState({});
  const [commentInputVisible, setCommentInputVisible] = useState({}); // To track visibility of comment input

  const handleLike = (id) => {
    setLiked(prevLiked => ({
      ...prevLiked,
      [id]: !prevLiked[id],
    }));
  };

  const handleCommentChange = (id, text) => {
    setComments(prevComments => ({
      ...prevComments,
      [id]: text,
    }));
  };

  const toggleCommentInput = (id) => {
    setCommentInputVisible(prev => ({
      ...prev,
      [id]: !prev[id],
    }));
  };

  const renderItem = ({ item }) => (
    <View style={styles.post}>
      <View style={styles.header}>
        <Image source={{ uri: item.image }} style={styles.avatar} />
        <Text style={styles.name}>{item.name}</Text>
      </View>
      <Text style={styles.text}>{item.text}</Text>

      {/* Post Image */}
      <Image source={{ uri: item.postImage }} style={styles.postImage} />

      {/* Like Button */}
      <TouchableOpacity onPress={() => handleLike(item.id)}>
        <Text style={styles.likeButton}>
          {liked[item.id] ? '‚ù§Ô∏è Liked' : 'ü§ç Like'}
        </Text>
      </TouchableOpacity>

      {/* Comment Button */}
      <TouchableOpacity onPress={() => toggleCommentInput(item.id)}>
        <Text style={styles.commentButton}>üí¨ Comment</Text>
      </TouchableOpacity>

      {/* Show Comment Input when Comment Button is pressed */}
      {commentInputVisible[item.id] && (
        <TextInput
          style={styles.commentInput}
          placeholder="Add your comment"
          value={comments[item.id] || ''}
          onChangeText={(text) => handleCommentChange(item.id, text)}
        />
      )}
    </View>
  );

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Social Media App</Text>
      <FlatList
        data={data}
        renderItem={renderItem}
        keyExtractor={(item) => item.id}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#dfe6e9', // Light grayish background
    paddingTop: 20,
    paddingHorizontal: 15,
  },
  title: {
    fontSize: 28,
    color: '#2d3436', // Dark text color for title
    fontWeight: 'bold',
    marginBottom: 15,
  },
  post: {
    backgroundColor: '#ffffff', // White background for posts
    marginBottom: 15,
    borderRadius: 10,
    padding: 15,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 8,
    elevation: 5, // For better shadow on Android
  },
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
  },
  avatar: {
    width: 50,
    height: 50,
    borderRadius: 25,
    marginRight: 10,
  },
  name: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#2d3436', // Dark text color for name
  },
  text: {
    fontSize: 16,
    color: '#636e72', // Lighter gray color for post text
  },
  postImage: {
    width: '100%',
    height: 250,
    borderRadius: 10,
    marginVertical: 10,
  },
  likeButton: {
    fontSize: 18,
    color: '#ff4d4d', // Red color for like button
    marginTop: 10,
  },
  commentButton: {
    fontSize: 18,
    color: '#007bff', // Blue color for comment button
    marginTop: 10,
  },
  commentInput: {
    marginTop: 10,
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
    padding: 10,
    fontSize: 16,
  },
});

export default App;

